                if ($consulta) {
                    if ($consulta->num_rows > 0) {
                        // Mostrar los resultados en tarjetas
                        while ($row = $consulta->fetch_assoc()) {
                            echo '<div class="card">';
                                $imagenPath = $row['imagen'];
                                if (file_exists($imagenPath)) {
                                    echo '<img src="' . $imagenPath . '" alt="' . $row['nombre'] . '">';
                                } else {
                                    echo '<p>Error: La imagen no existe en la ruta especificada.</p>';
                                }
                                echo '<h3>' . $row['nombre'] . '</h3>';
                                echo '<p class="descripcion_trunc">' . $row['descripcion'] . '</p>';
                                echo '<div class="cat_y_rating">';
                                    echo '<p>Categoría: ' . $row['categoria'] . '</p>';
                                    echo '<p>Rating: ' . $row['rating'] . '</p>';
                                echo '</div>';
                                echo '<div class="btn_Reserva">';
                                    echo '<p>Precio: $' . $row['precio'] . '</p>';
                                    echo '<button class="reserva-button">Reservar</button>';
                                echo '</div>';
                            echo '</div>';
                        }
                    } else {
                        echo "No se encontraron hospedajes.";
                    }

                    // Cerrar la consulta
                    $consulta->close();
                } else {
                    // Aquí manejas el caso en que la consulta falla
                    echo "Error al consultar hospedajes.";
